import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Table from "react-bootstrap/Table";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import Modal from "react-bootstrap/Modal";
import axios from "axios";

function Bookings({ role, email, name, signOut }) {
  const [tickets, setTickets] = useState([]);
  const [showBookings, setShowBookings] = useState(false);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [showDeleteNotification, setShowDeleteNotification] = useState(false);


  const showConfirmModal = () => {
    if (selectedTicket) {
      setShowDeleteNotification(true);
    }
  };

  // should delete the ticket from specific user in user database
  // and should make that seat for the flight available
  const cancelTicket = () => {
    // axios.delete('YOUR_ENDPOINT', {
    //   data: {
    //     flightNumber: selectedTicket.flightNumber,
    //     seat: selectedTicket.seat
    //   }
    // })
    //   .then(response => {
    //     if (response.status === 200) {
    //       setShowDeleteNotification(false);
    //       if (selectedTicket) {
    //         const updatedTickets = tickets.filter(
    //           // need to delete based on both flight number and seat number
    //           (ticket) =>
    //             ticket.flightNumber !== selectedTicket.flightNumber ||
    //             ticket.seat !== selectedTicket.seat
    //         );
    //         setTickets(updatedTickets);
    //         setSelectedTicket(null);
    //       }
    //     }
    //   })
    //   .catch(error => {
    //     console.error('Error deleting ticket:', error);
    //   });

    setShowDeleteNotification(false);
    if (selectedTicket) {
      const updatedTickets = tickets.filter(
        // need to delete based on both flight number and seat number
        (ticket) =>
          ticket.flightNumber !== selectedTicket.flightNumber ||
          ticket.seat !== selectedTicket.seat
      );
      setTickets(updatedTickets);
      setSelectedTicket(null);
    }
  };

  const closeConfirmModal = () => {
    setShowDeleteNotification(false);
  };

  // should return an list of tickets
  useEffect(() => {
    axios.post('http://localhost:8080/user/getUsersSeats',{ 
      email: email
    })
       .then(response => {
         if (response.status === 200) {  // valid response from database
          setTickets(response.data) 
          console.log(response);
         }
       })
       .catch(error => {
         console.error('Error fetching flights:', error);
       });
    // setTickets([
    //   {
    //     flightNumber: 1,
    //     departure: "New York",
    //     destination: "Los Angeles",
    //     departureTime: "2023-11-20T08:00:00Z",
    //     arrivalTime: "2023-11-20T11:00:00Z",
    //     seat: "1A",
    //     price: 100,
    //     seatType: "Business",
    //   },
    //   {
    //     flightNumber: 1,
    //     departure: "New York",
    //     destination: "Los Angeles",
    //     departureTime: "2023-11-20T08:00:00Z",
    //     arrivalTime: "2023-11-20T11:00:00Z",
    //     seat: "1B",
    //     price: 100,
    //     seatType: "Business",
    //   },
    //   {
    //     flightNumber: 2,
    //     departure: "London",
    //     destination: "Paris",
    //     departureTime: "2023-11-21T10:30:00Z",
    //     arrivalTime: "2023-11-21T12:00:00Z",
    //     seat: "1A",
    //     price: 50,
    //     seatType: "Regular",
    //   },
    // ]);
  }, []);

  const formattedTime = (dateTimeString) => {
    const options = {
      year: "numeric",
      month: "numeric",
      day: "numeric",
      hour: "numeric",
      minute: "numeric",
      second: "numeric",
      timeZoneName: "short",
    };
    return new Date(dateTimeString).toLocaleString("en-US", options);
  };

  const handleTicketSelection = (ticket) => {
    setSelectedTicket(ticket);
  };

  return (
    <>
      <div className="d-flex justify-content-between align-items-center">
        <h1>My Bookings</h1>
        <div>
          <Button variant="danger" onClick={showConfirmModal}>
            Cancel Ticket
          </Button>
        </div>
      </div>
      <Table hover>
        <colgroup>
          <col style={{ width: "5%" }} /> {/* Flight Number */}
          <col style={{ width: "10%" }} /> {/* Departure */}
          <col style={{ width: "10%" }} /> {/* Destination */}
          <col style={{ width: "20%" }} /> {/* Departure Time */}
          <col style={{ width: "20%" }} /> {/* Arrival Time */}
          <col style={{ width: "5%" }} /> {/* Seat */}
          <col style={{ width: "5%" }} /> {/* Ticket Price */}
          <col style={{ width: "7%" }} /> {/* Seat Class */}
        </colgroup>
        <thead>
          <tr>
            <th>Flight Number</th>
            <th>Origin</th>
            <th>Destination</th>
            <th>Departure Time</th>
            <th>Arrival Time</th>
            <th>Seat</th>
            <th>Ticket Price</th>
            <th>Seat Class</th>
          </tr>
        </thead>
        <tbody>
          {tickets.map((ticket, index) => (
            <tr
              key={index}
              onClick={() => handleTicketSelection(ticket)}
              style={{
                cursor: "pointer",
              }}
            >
              <td>{ticket.flightNumber}</td>
              <td>{ticket.departure}</td>
              <td>{ticket.destination}</td>
              <td>{formattedTime(ticket.departureTime)}</td>
              <td>{formattedTime(ticket.arrivalTime)}</td>
              <td>{ticket.seatNumber}</td>
              <td>{ticket.price}</td>
              <td>{ticket.seatType}</td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Modal show={showDeleteNotification} onHide={closeConfirmModal}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Flight Cancellation?</Modal.Title>
        </Modal.Header>
        <Modal.Body>Are you sure you want to cancel this flight?</Modal.Body>
        {selectedTicket && (
          <Modal.Body>
            Flight {selectedTicket.flightNumber} from {selectedTicket.departure}{" "}
            to {selectedTicket.destination} <br />
            Seat: {selectedTicket.seat}, Price: {selectedTicket.price}, Seat
            Class: {selectedTicket.seatType}
          </Modal.Body>
        )}
        <Modal.Footer>
          <Button variant="secondary" onClick={closeConfirmModal}>
            Close
          </Button>
          <Button variant="danger" onClick={cancelTicket}>
            Confirm Cancellation
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Bookings;
