import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Form,
  Button,
  Modal,
} from "react-bootstrap";
import axios from "axios";

function Payment({ goBack, chosenFlight, selectedSeats, role, email, name }) {
  const seatNumbers = selectedSeats.map((seat) => seat.seatNumber).join(", ");
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [totalPrice, setTotalPrice] = useState(
    selectedSeats.reduce((total, seat) => (total + seat.price) * 1.05, 0)
  );
  const [hasTicketInsurance, setHasTicketInsurance] = useState(false);

  const calculatePrice = () => {
    const subTotal = selectedSeats.reduce(
      (total, seat) => total + seat.price,
      0
    );
    const tax = subTotal * 0.05;
    let updatedPrice = subTotal + tax;

    if (hasTicketInsurance) {
      updatedPrice += 100;
    }

    setTotalPrice(updatedPrice);
  };

  const [creditCard, setCreditCard] = useState({
    cardNumber: "",
    expiryDate: "",
    cvv: "",
  });

  const handleConfirmPurchase = () => {
    // axios
    //   .post("YOUR_ENDPOINT", {
    //     flightID: chosenFlight.flightNumber,
    //     seats: selectedSeats,
    //     ticketInsurance: hasTicketInsurance
    //   })
    //   .then((response) => {
    //     if (response.status === 200) {
    //       // valid response from database
    //       // load flights homepage
    //     }
    //   })
    //   .catch((error) => {
    //     console.error("Error fetching tickets:", error);
    //   });
    console.log(chosenFlight.flightNumber, selectedSeats, hasTicketInsurance)
    setShowConfirmation(false);
  };

  const handleSubmit = () => {
    setShowConfirmation(true);
  };

  const handleTicketInsurance = () => {
    setHasTicketInsurance(!hasTicketInsurance);
  };

  useEffect(() => {
    calculatePrice();
  }, [selectedSeats, hasTicketInsurance]);

  return (
    <>
      <div className="d-flex justify-content-between align-items-center">
        <h1>
          Flight {chosenFlight.flightNumber} from {chosenFlight.departure} to{" "}
          {chosenFlight.destination}
        </h1>
      </div>
      <Row className="justify-content-center">
        <Col md="9" lg="7" xl="5">
          <Card>
            <Card.Body>
              <Card.Title className="d-flex justify-content-between mb-0">
                <p className="text-muted mb-0">Selected Seats: {seatNumbers}</p>
                <p className="mb-0">${totalPrice} (tax included)</p>
              </Card.Title>
            </Card.Body>
            <div className="rounded-bottom" style={{ backgroundColor: "#eee" }}>
              <Card.Body>
                <Row className="justify-content-center">
                  <Col xs="10">
                    {selectedSeats.map((seat, index) => (
                      <div key={index}>
                        <p>
                          Seat Number: {seat.seatNumber}, Seat Type:{" "}
                          {seat.seatType}, Seat Price: ${seat.price}.
                        </p>
                      </div>
                    ))}
                  </Col>
                  <Col xs="10">
                    <Form>
                      <Form.Group controlId="formCardNumber">
                        <Form.Label>Credit Card Number</Form.Label>
                        <Form.Control
                          type="text"
                          placeholder="1234 5678 1234 5678"
                          onChange={(e) =>
                            setCreditCard({
                              ...creditCard,
                              cardNumber: e.target.value,
                            })
                          }
                        />
                      </Form.Group>
                      <Row className="mb-3">
                        <Col>
                          <Form.Group controlId="formExpire">
                            <Form.Label>Credit Card Expiry Date</Form.Label>
                            <Form.Control
                              type="text"
                              placeholder="MM/YYYY"
                              onChange={(e) =>
                                setCreditCard({
                                  ...creditCard,
                                  expiryDate: e.target.value,
                                })
                              }
                            />
                          </Form.Group>
                        </Col>
                        <Col>
                          <Form.Group controlId="formCVV">
                            <Form.Label>Credit Card CVV</Form.Label>
                            <Form.Control
                              type="text"
                              placeholder="CVV"
                              onChange={(e) =>
                                setCreditCard({
                                  ...creditCard,
                                  cvv: e.target.value,
                                })
                              }
                            />
                          </Form.Group>
                        </Col>
                      </Row>
                      <Button
                        variant="primary"
                        style={{ marginLeft: "20px", marginRight: "5px" }}
                        type="submit"
                        onClick={handleSubmit}
                      >
                        Confirm Payment
                      </Button>
                      <Button
                        onClick={() => goBack(selectedSeats)}
                        variant="primary"
                        style={{ marginLeft: "5px", marginRight: "5px" }}
                      >
                        Cancel Payment
                      </Button>
                      <Button
                        variant="primary"
                        onClick={handleTicketInsurance}
                        style={{ marginLeft: "5px", marginRight: "5px" }}
                      >
                        {hasTicketInsurance
                          ? "Cancel Insurance"
                          : "Add Ticket Insurance"}
                      </Button>
                    </Form>
                  </Col>
                </Row>
              </Card.Body>
            </div>
          </Card>
        </Col>
      </Row>
      <Modal show={showConfirmation} onHide={() => setShowConfirmation(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Purchase</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Confirm purchase of Flight {chosenFlight.flightNumber} from{" "}
          {chosenFlight.departure} to {chosenFlight.destination}?
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => setShowConfirmation(false)}
          >
            Close
          </Button>
          <Button variant="primary" onClick={handleConfirmPurchase}>
            Confirm Purchase
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Payment;
